
public class Teste {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte j = 60;
		short k = 24;
		int l = 30;
		long m = 12L;
		long resultado = 0L;
		
		System.out.println(resultado);
		resultado += j;
		System.out.println(resultado);
		resultado += k;
		System.out.println(resultado);
		resultado /= m;
		System.out.println(resultado);
		resultado -= l;
		System.out.println(resultado);
		resultado = -resultado;
		System.out.println(resultado);
		resultado %= m;
		System.out.println(resultado);

	}

}
